import Moya

enum MovieProvider {
    case upcoming(page: Int, language: String, region: String)
    case details(movieId: Int, language: String)
    case availableGenres(language: String)
    case search(query: String, page: Int, language: String)
}

extension MovieProvider: TargetType {
    
    var baseURL: URL { return URL(string: KeyProvider.getTMDb("baseUrl"))!.appendingPathComponent(KeyProvider.getTMDb("apiVersion")) }
    
    var path: String {
        switch self {
        case .upcoming:
            return "/movie/upcoming"
        case .details(let movieId, _):
            return "/movie/\(movieId)"
        case .availableGenres:
            return "/genre/movie/list"
        case .search:
            return "/search/movie"
        }
    }
    
    var method: Moya.Method {
        switch self {
        case .upcoming, .details, .availableGenres, .search:
            return .get
        }
    }
    
    var task: Task {
        switch self {
        case .upcoming(let page, let language, let region):
            return .requestParameters(parameters: ["api_key": KeyProvider.getTMDb("apiKey"),
                                                   "language": language,
                                                   "page": page,
                                                   "region": region],
                                      encoding: URLEncoding.default)
        case .details(_, let language):
            return .requestParameters(parameters: ["api_key": KeyProvider.getTMDb("apiKey"),
                                                   "language": language],
                                      encoding: URLEncoding.default)
        case .availableGenres(let language):
            return .requestParameters(parameters: ["api_key": KeyProvider.getTMDb("apiKey"),
                                                   "language": language],
                                      encoding: URLEncoding.default)
        case .search(let query, let page, let language):
            return .requestParameters(parameters: ["api_key": KeyProvider.getTMDb("apiKey"),
                                                   "language": language,
                                                   "page": page,
                                                   "query": query],
                                      encoding: URLEncoding.default)
        }
    }
    
